def overlay_blend_8(base: List[List[int]], active: List[List[int]]) -> List[List[int]]:
    return matrix_where(
        base,
        matrix_scalar_div(32, matrix_elemwise_mul(base, base)),
        lambda x, y: ite(
            x >= 16,
            reduce_sum([
                vec_scalar_mul(2, [x]),
                [x],
                vec_scalar_mul(-2, vec_scalar_div(32, vec_elemwise_mul([x], [x]))),
                [-32]
            ])[0],
            reduce_sum([vec_scalar_mul(2, vec_scalar_div(32, vec_elemwise_mul([x], [x])))])[0]
        )
    )
