def color_burn_8(base: List[List[int]], active: List[List[int]]) -> List[List[int]]:
    return matrix_where(
        active,
        matrix_scalar_sub(
            32,
            scalar_matrix_div(
                32,
                matrix_where(
                    active,
                    matrix_scalar_sub(32, base),
                    lambda x, y: 1
                )
            )
        ),
        lambda x, y: 32
    )
