def invariant1(row: int, base: List[List[int]], active: List[List[int]], out: List[List[int]], rand_cons: int) -> bool:
    return row >= 0 and row < len(base) and out == matrix_elemwise_sub(base[:row], active[:row])

def invariant2(row: int, col: int, base: List[List[int]], active: List[List[int]], row_vec: List[int], out: List[List[int]], rand_cons: int) -> bool:
    return row >= 0 and row < len(base) and col >= 0 and col < len(base[0]) and row_vec == vec_elemwise_sub(base[row][:col], active[row][:col]) and out == matrix_elemwise_sub(base[:row], active[:row])
