def invariant1(row, base, active, out):
    return (0 <= row < len(base)) and (out == matrix_elemwise_add(base[:row], active[:row]))



def invariant2(row, col, base, active, row_vec, out):
    return (0 <= row < len(base)) and (0 <= col < len(base[0])) and \
           (row_vec[:col] == vec_elemwise_add(base[row][:col], active[row][:col])) and \
           (out == matrix_elemwise_add(base[:row], active[:row]))
