def adi_part1(n: int, X: List[List[int]], A: List[List[int]], B: List[List[int]]) -> List[List[int]]:
    return matrix_col_slice(
        matrix_elemwise_sub(
            matrix_col_slice(X, 1, n),
            matrix_elemwise_div(
                matrix_elemwise_mul(
                    matrix_col_slice(X, 0, n-1),
                    matrix_col_slice(A, 1, n)
                ),
                matrix_col_slice(B, 0, n-1)
            )
        ),
        0, n-1
    )
