def adi_part1(
    n: int, X: List[List[int]], A: List[List[int]], B: List[List[int]]
) -> List[List[int]]:
    return matrix_row_slice(
        matrix_where(
            X,
            matrix_elemwise_div(
                matrix_elemwise_mul(
                    matrix_col_slice(X, 0, len(X[0]) - 1),
                    matrix_col_slice(A, 1, len(A[0])),
                ),
                matrix_col_slice(B, 0, len(B[0]) - 1),
            ),
            lambda x, y: x - y,
        ),
        0,
        len(X),
    )
