def fdtd_2d_part3(
    nx: int, ny: int, ex: List[List[int]], ey: List[List[int]], hz: List[List[int]]
) -> List[List[int]]:
    return matrix_row_slice(
        matrix_where(
            matrix_row_slice(hz, 0, nx - 1),
            matrix_elemwise_add(
                matrix_elemwise_sub(
                    matrix_col_slice(ex, 1, ny), matrix_col_slice(ex, 0, ny - 1)
                ),
                matrix_elemwise_sub(
                    matrix_row_slice(ey, 1, nx), matrix_row_slice(ey, 0, nx - 1)
                ),
            ),
            lambda x, y: x - 7 * y,
        ),
        0,
        nx - 1,
    )
