from typing import List

def color_burn_8(base: List[List[int]], active: List[List[int]]) -> List[List[int]]:
    return matrix_where(
        matrix_scalar_sub(
            255,
            matrix_scalar_div(
                255,
                matrix_elemwise_div(
                    matrix_scalar_sub(255, base),
                    ite(
                        matrix_elemwise_sub(active, [[0]*len(active[0])]*len(active)),
                        active,
                        [[1]*len(active[0])]*len(active)
                    )
                )
            )
        ),
        [[255]*len(base[0])]*len(base),
        lambda x, y: ite(x != 255, x, y)
    )
