from typing import List


def dissolve_blend_8(base: List[List[int]], active: List[List[int]], opacity: int, rand_cons: int) -> List[List[int]]:
    return matrix_where(
        active,
        base,
        lambda active_elem, base_elem: ite(
            opacity - (((rand_cons % 100) + 1) // 100) >= 0,
            active_elem,
            base_elem
        )
    )
