def adi_part4(n: int, X: List[List[int]], A: List[List[int]], B: List[List[int]]) -> List[List[int]]:
    return matrix_elemwise_div(
        matrix_elemwise_sub(
            matrix_col_slice(X, 1, n-1),
            vec_scalar_mul(
                matrix_elemwise_mul(
                    matrix_col_slice(X, 0, n-2),
                    matrix_col_slice(A, 0, n-2)
                ),
                -1
            )
        ),
        matrix_col_slice(B, 0, n-2)
    )
